mapper.select:
		select `id`,`parentid`,`tokenid`,`balance`,`type`,`state`,`version`,`createdate` from wallet_plataccount

		  		   and id = #{id} 
		  		   and parentId = #{parentId} 
		  		   and tokenId = #{tokenId} 
		  		   and balance = #{balance} 
		  		   and type = #{type} 
		  		   and state = #{state} 
		  		   and version = #{version} 
		  		   and createdate = #{createdate} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from wallet_plataccount

  		   and id = #{id} 
  		   and parentId = #{parentId} 
  		   and balance = #{balance} 
  		   and type = #{type} 
  		   and state = #{state} 
  		   and version = #{version} 
  		   and createdate = #{createdate}

mapper.update:
  update wallet_plataccount 

		balance = #{balance}, version = #{version}

  where id = #{id} and state = 1 and version = #{lockedVersion}

update wallet_plataccount a, wallet_plataccount b 

      a.balance = #{frombalance}, a.version = #{accountVersion}, b.balance = #{tobalance}, b.version = #{toAccountVersion}

where a.id = #{accountid} and a.version = #{lockedVersion} and b.id = #{toAccountId} and b.version = #{toLockedVersion} and a.tokenid = #{tokenId} and b.tokenid=#{tokenId} and a.state = 1 and b.state=1
