mapper.select:
		select `id`,`accountid`,`accountVersion`,`toAccountId`,`toAccountVersion`,`tokentype`,`amount`,`frombalance`,`tobalance`,`fee`,`state`,`opstype`,`remark`,`json`,`createdate` from wallet_plattransaction

		  		   and id = #{id} 
		  		   and accountid = #{accountid} 
		  		   and accountVersion = #{accountVersion} 
		  		   and toAccountId = #{toAccountId} 
		  		   and toAccountVersion = #{toAccountVersion} 
		  		   and tokentype = #{tokentype} 
		  		   and amount = #{amount} 
		  		   and fee = #{fee} 
		  		   and state = #{state} 
		  		   and opstype = #{opstype} 
		  		   and createdate = #{createdate} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

		select count(*) from wallet_plattransaction

		  		   and id = #{id} 
		  		   and accountid = #{accountid} 
		  		   and accountVersion = #{accountVersion} 
                   and toAccountId = #{toAccountId} 
                   and toAccountVersion = #{toAccountVersion} 
		  		   and tokentype = #{tokentype} 
		  		   and amount = #{amount} 
		  		   and fee = #{fee} 
		  		   and state = #{state} 
		  		   and opstype = #{opstype} 
		  		   and createdate = #{createdate}
